import React, { Component, useState } from 'react';
import { Map, TileLayer, withLeaflet } from 'react-leaflet';
import { ReactLeafletSearch } from 'react-leaflet-search';
import { Button, Col, Container, Form, Row } from 'react-bootstrap';

class CustomOpenStreetMap {
  constructor(options = { providerKey: null, searchBounds: [] }) {
    let { searchBounds } = options;
    //Bounds are expected to be a nested array of [[sw_lat, sw_lng],[ne_lat, ne_lng]].
    // We convert them into a string of 'x1,y1,x2,y2' which is the opposite way around from lat/lng - it's lng/lat
    let boundsUrlComponent = '';
    let regionUrlComponent = '';
    if (searchBounds.length) {
      const reversed = searchBounds.map((el) => {
        return el.reverse();
      });
      this.bounds = [].concat([], ...reversed).join(',');
      boundsUrlComponent = `&bounded=1&viewbox=${this.bounds}`;
    }
    if ('region' in options) {
      regionUrlComponent = `&countrycodes=${options.region}`;
    }
    this.url = `https://nominatim.openstreetmap.org/search?format=json&addressdetails=1&polygon_svg=1&namedetails=1${boundsUrlComponent}${regionUrlComponent}&q=`;
  }

  async search(query) {
    // console.log(this.url + query)
    const response = await fetch(this.url + query).then((res) => res.json());
    return this.formatResponse(response);
  }

  formatResponse(response) {
    const resources = response;
    const count = response.length;
    const info =
      count > 0
        ? resources.map((e) => ({
            bounds: e.boundingbox.map((bound) => Number(bound)),
            latitude: 0,
            longitude: 0,
            name: 'deneme 1',
          }))
        : 'Not Found';
    return {
      info: info,
      raw: response,
    };
  }
}

export default class SimpleExample extends Component {
  constructor(props) {
    super(props);
    this.provider = new CustomOpenStreetMap();
    this.state = {
      count: 0,
      maxZoom: 20,
      bounds: [
        {
          lat: 0,
          lng: 0,
        },
      ],
    };
  }

  render() {
    const ReactLeafletSearchComponent = withLeaflet(ReactLeafletSearch);

    const [latitude, setLatitude] = useState('43.296482');
    const [longitude, setLongitude] = useState('5.36978');

    const handleInputLatitudeChange = (event) => {
      const { value } = event.target;
      setLatitude(value);
    };

    const handleInputLongitudeChange = (event) => {
      const { value } = event.target;
      setLongitude(value);
    };

    const update = () => {};

    return (
      <div>
        <Form>
          <Form.Group controlId="coord">
            <Form.Label>Recherche par coordonn√©es</Form.Label>
            <Row>
              <Col>
                Latitude
                <Form.Control
                  type="text"
                  placeholder="Saisir la latitude"
                  value={latitude}
                  onChange={handleInputLatitudeChange}
                />
              </Col>
              <Col>
                Longitude
                <Form.Control
                  type="text"
                  placeholder="Saisir la longitude"
                  value={longitude}
                  onChange={handleInputLongitudeChange}
                />
              </Col>
            </Row>
          </Form.Group>

          <Button variant="warning" onClick={update}>
            Envoyer
          </Button>
        </Form>
        <br />
        <Map
          className="simpleMap"
          scrollWheelZoom={true}
          bounds={this.state.bounds}
          maxZoom={this.state.maxZoom}
        >
          <TileLayer
            noWrap={true}
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <ReactLeafletSearchComponent
            customProvider={this.provider}
            inputPlaceholder="Custom placeholder"
            search={[43.296482, 5.36978]}
            showMarker={true}
            zoom={11}
            showPopup={true}
            closeResultsOnClick={true}
            openSearchOnLoad={false}
          />
        </Map>
      </div>
    );
  }
}
