import React, { useState } from 'react';
import { Button, Col, Container, Form, Row, Table } from 'react-bootstrap';
import { Map, TileLayer, Marker } from 'react-leaflet';
import { Icon } from 'leaflet';
import 'leaflet/dist/leaflet.css';

export const icon = new Icon({
  iconUrl: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/134893/pin-red.svg',
  iconSize: [25, 25],
});

const Home = () => {
  const [latitude, setLatitude] = useState('43.296482');
  const [longitude, setLongitude] = useState('5.36978');
  //const [data, setData] = useState([]);

  const handleInputLatitudeChange = (event) => {
    const { value } = event.target;
    setLatitude(value);
  };

  const handleInputLongitudeChange = (event) => {
    const { value } = event.target;
    setLongitude(value);
  };

  return (
    <Container className="homePage">
      <Form>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Recherche par ville</Form.Label>
          <Form.Control type="text" placeholder="Saisir le nom d'une ville" />
        </Form.Group>

        <Button variant="warning" type="submit">
          Envoyer
        </Button>
      </Form>

      <br />
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th></th>
            <th>Villes</th>
            <th>Latitude</th>
            <th>Longitude</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Marseille</td>
            <td>43.296482</td>
            <td>@5.36978</td>
          </tr>
        </tbody>
      </Table>

      <br />

      <Form>
        <Form.Group controlId="coord">
          <Form.Label>Recherche par coordonnées</Form.Label>
          <Row>
            <Col>
              Latitude
              <Form.Control
                type="text"
                placeholder="Saisir la latitude"
                value={latitude}
                onChange={handleInputLatitudeChange}
              />
            </Col>
            <Col>
              Longitude
              <Form.Control
                type="text"
                placeholder="Saisir la longitude"
                value={longitude}
                onChange={handleInputLongitudeChange}
              />
            </Col>
          </Row>
        </Form.Group>
      </Form>
      <br />
      <Map center={[latitude, longitude]} zoom={13} scrollWheelZoom={false}>
        <TileLayer
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[latitude, longitude]} icon={icon} />
        <Marker position={[43.396482, 5.36978]} icon={icon} />
      </Map>
    </Container>
  );
};
export default Home;
